package api.javazs.com.myapi.coderhub.controller;import api.javazs.com.myapi.coderhub.entity.QuestionEntity;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.ArrayList;import java.util.List;/** * @author : liuwenlong * @date : 2018/2/25 14:44 */@RequestMapping(value = "/coder-hub")@RestController@Api(value = "首页")public class IndexController {    @PostMapping    @ApiOperation("首页")    public List<QuestionEntity> index(){        List<QuestionEntity> list = new ArrayList<>();        for (int i=0;i<10;i++){            QuestionEntity questionEntity = new QuestionEntity();            questionEntity.setTitle("测试题目");            questionEntity.setContent("lllll");            questionEntity.setId(i);            list.add(questionEntity);        }        return list;    }    @PostMapping(value = "list")    @ApiOperation("面试题列表")    public List<String> getList(){        List<String> list = new ArrayList<>();        for (int i=0;i<10;i++){            list.add("测试题目"+i);        }        return list;    }}